pragma solidity 0.6.12;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./uniswapv2/interfaces/IWETH.sol";

import "./ApexToken.sol";
import "./TopReferrer.sol";
import "./ExchangeConnector.sol";


contract ApexMain is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt.
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 poolToken;           // Address of LP token contract.
        uint256 allocPoint;       // How many allocation points assigned to this pool.
        uint256 lastRewardBlock;  // Last block number that APEX distribution occurs.
        uint256 accApexPerShare; // Accumulated APEX per share, times 1e12. See below.
    }

    ApexToken public apex;
    TopReferrer public topReferrer;
    ExchangeConnector public connector;
    address initialReferral;
    address payable devAddr;

    bool public LPGenerationCompleted;

    // reward decrease period time
    uint256 public constant DURATION = 1 weeks;
    uint256 public constant TOTAL_DURATION = 6 weeks;
    // initial amount of Apex
    uint256 public initReward = 2555 * 1e18;
    bool public haveStarted = false;
    // next time of reward decrease
    uint256 public decreaseRewardTime = 0;
    uint256 public stableRewardStartTime = 0;
    uint256 public decreaseRewardRate = 1;
    uint256 public lastUpdateTime = 0;
    uint256 public lastDistributeTime = 0;
    // distribution of per second
    uint256 public rewardRate = 0;
    uint256 public rewardPerLPToken = 0;

    // avg ethereum block speed
    uint256 public constant BLOCK_PER_DAY = 6450;
    uint256 public constant SECONDS_PER_DAY = 86400;

    uint256 public totalLPTokensMinted;
    uint256 public totalWhitelistContributed;
    uint256 public totalPrizePool;
    uint256 public LPperETHUnit;
    address[] public initialWhitelistParticipants;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;
    mapping (address => address) public referrerInfo;
    mapping (address => uint)  public whitelistContributed;
    // Total allocation poitns. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;

    event Start(uint amount);
    event RewardDecrease(uint amount);
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event LiquidityAddition(address indexed dst, uint value);

    constructor(
        ApexToken _apex,
        TopReferrer _referrer,
        ExchangeConnector _connector,
        address _initialReferral
    ) public {
        apex = _apex;
        topReferrer = _referrer;
        connector = _connector;
        initialReferral = _initialReferral;
        initialWhitelistParticipants.push(initialReferral);
        referrerInfo[initialReferral] = initialReferral;
    }

    function setDevAddress(address payable account) public onlyOwner {
        devAddr = account;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function startFarming() external onlyOwner {
        liquidityGenerationUniswapAPEXxWETHPair();
        rewardRate = initReward.div(DURATION);
        apex.mint(address(this), initReward);

        lastUpdateTime = block.timestamp;
        lastDistributeTime = block.timestamp;
        decreaseRewardTime = block.timestamp.add(DURATION);
        stableRewardStartTime = block.timestamp.add(TOTAL_DURATION);

        for (uint i = 0; i < initialWhitelistParticipants.length; i++) {
            address account = initialWhitelistParticipants[i];
            deposit(0, whitelistContributed[account].mul(LPperETHUnit).div(1e18),
            referrerInfo[account]);
        }

        haveStarted = true;
        emit Start(initReward);
    }

    function checkRewardDecrease() internal {
        if (stableRewardStartTime > block.timestamp && block.timestamp >= decreaseRewardTime) {
            initReward = initReward.mul(100 - decreaseRewardRate * 10).div(100);
            decreaseRewardRate += 1;
            apex.mint(address(this), initReward);

            rewardRate = initReward.div(DURATION);
            decreaseRewardTime = decreaseRewardTime.add(DURATION);

            emit RewardDecrease(initReward);
        }
    }

    function distributePrizePool() public onlyOwner() {
        topReferrer.distributePrizePool();
    }

    // Add a new token to the pool. Can only be called by the owner.
    // XXX DO NOT add the same token more than once. Rewards will be messed up if you do.
    function add(uint256 _allocPoint, IERC20 _poolToken, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({
        poolToken: _poolToken,
        allocPoint: _allocPoint,
        lastRewardBlock: lastRewardBlock,
        accApexPerShare: 0
        }));
    }

    function pendingApex(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accApexPerShare = pool.accApexPerShare;
        uint256 lpSupply = pool.poolToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 numBlocks = block.number.sub(pool.lastRewardBlock);
            uint256 rewardPerBlock = rewardRate.mul(SECONDS_PER_DAY).div(BLOCK_PER_DAY);
            uint256 apexReward = numBlocks.mul(rewardPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accApexPerShare = accApexPerShare.add(apexReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accApexPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 tokenSupply = pool.poolToken.balanceOf(address(this));
        if (tokenSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 numBlocks = block.number.sub(pool.lastRewardBlock);
        uint256 rewardPerBlock = rewardRate.mul(SECONDS_PER_DAY).div(BLOCK_PER_DAY);
        uint256 apexReward = numBlocks.mul(rewardPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
        pool.accApexPerShare = pool.accApexPerShare.add(apexReward.mul(1e12).div(tokenSupply));
        pool.lastRewardBlock = block.number;
    }

    function depositWhitelist(address referrerAddress) public payable {
        require(referrerAddress != address(0) && referrerAddress != msg.sender, "Invalid referrer address");
        whitelistContributed[msg.sender] += msg.value; // Overflow protection from safemath is not needed here
        require(whitelistContributed[msg.sender] <= 5 * 1e18, "Whitelist contribution amount exceeds maximum");

        if(LPGenerationCompleted == false) {
            totalWhitelistContributed = totalWhitelistContributed.add(msg.value); // for front end display during LGE
            initialWhitelistParticipants.push(msg.sender);
            referrerInfo[msg.sender] = referrerAddress;
            emit LiquidityAddition(msg.sender, msg.value);
        } else {
            uint ethReceived = msg.value;
            uint devPortion = msg.value.mul(4).div(100);
            devAddr.transfer(devPortion);
            uint prizePoolPortion = msg.value.mul(7).div(100);
            topReferrer.dep{value: prizePoolPortion}();
            uint ethRemaining = ethReceived.sub(devPortion).sub(prizePoolPortion);
            uint ethToConvert = ethRemaining.div(2);
            uint ethToTransfer = ethRemaining - ethToConvert;
            uint[] memory amounts = connector.uniswapRouterV2().swapExactETHForTokens{value: ethToConvert}(0,
                connector.getPathForETHToApex(), address(this), block.timestamp+15);
            uint apexAmount = amounts[amounts.length - 1];
            address WETH = connector.uniswapRouterV2().WETH();
            IWETH(WETH).deposit{value : ethToTransfer}();
            IWETH(WETH).transfer(address(connector.pair()),ethToTransfer);
            safeApexTransfer(address(connector.pair()), apexAmount);
            connector.pair().mint(msg.sender);
            uint256 lPTokensMinted = connector.pair().balanceOf(msg.sender);
            require(lPTokensMinted != 0 , "LP creation failed");
        }
    }

    function liquidityGenerationUniswapAPEXxWETHPair() public {
        require(LPGenerationCompleted == false, "Liquidity generation already finished");
        totalPrizePool = address(this).balance.mul(7).div(100);
        devAddr.transfer(address(this).balance.mul(4).div(100));
        topReferrer.dep{value: totalPrizePool}();
        totalWhitelistContributed = address(this).balance;

        //Wrap eth
        address WETH = connector.uniswapRouterV2().WETH();
        IWETH(WETH).deposit{value : totalWhitelistContributed}();
        require(address(this).balance == 0 , "Transfer Failed");
        IWETH(WETH).transfer(address(connector.pair()),totalWhitelistContributed);
        apex.mint(address(this), 37000 * 1e18);
        safeApexTransfer(address(connector.pair()), apex.balanceOf(address(this)));
        connector.pair().mint(address(this));
        totalLPTokensMinted = connector.pair().balanceOf(address(this));
        require(totalLPTokensMinted != 0 , "LP creation failed");
        LPperETHUnit = totalLPTokensMinted.mul(1e18).div(totalWhitelistContributed); // 1e18x for  change

        require(LPperETHUnit != 0 , "LP creation failed");
        LPGenerationCompleted = true;
    }

    // Deposit tokens for APEX allocation.
    function deposit(uint256 _pid, uint256 _amount, address referrerAddress) public payable {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accApexPerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                safeApexTransfer(msg.sender, pending);
            }
        }
        checkRewardDecrease();
        if (_amount > 0) {
            if (_pid == 1) {
                uint ethReceived = msg.value;
                address WETH = connector.uniswapRouterV2().WETH();
                IWETH(WETH).deposit{value : ethReceived}();
                user.amount = user.amount.add(ethReceived);
                require(user.amount < 32 * 1e18, "Total deposit exceeds 32 ETH");
            } else {
                pool.poolToken.safeTransferFrom(address(msg.sender), address(this), _amount);
                uint256 amountToReferrer = _amount.mul(5).div(89);
                UserInfo storage referrer = userInfo[_pid][referrerAddress];

                if(user.amount > 0) {
                    topReferrer.updateLpAmount(msg.sender, _amount.sub(amountToReferrer));
                } else {
                    topReferrer.addUser(msg.sender, _amount.sub(amountToReferrer));
                }
                topReferrer.updateLpAmount(referrerAddress, amountToReferrer);

                referrer.amount = referrer.amount.add(amountToReferrer);
                user.amount = user.amount.add(_amount.sub(amountToReferrer));
            }
        }

        user.rewardDebt = user.amount.mul(pool.accApexPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw staked tokens.
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accApexPerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            safeApexTransfer(msg.sender, pending);
        }
        checkRewardDecrease();
        if(_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.poolToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accApexPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Safe APEX transfer function, just in case if rounding error causes pool to not have enough APEX.
    function safeApexTransfer(address _to, uint256 _amount) internal {
        uint256 apexBal = apex.balanceOf(address(this));
        if (_amount > apexBal) {
            apex.transfer(_to, apexBal);
        } else {
            apex.transfer(_to, _amount);
        }
    }
}
