import "@openzeppelin/contracts/access/Ownable.sol";

contract TopReferrer is Ownable {
    mapping(address => uint256) public lpAmounts;
    mapping(address => address) _nextSpot;
    uint256 public listSize;
    address constant GUARD = address(1);

    constructor() public {
        _nextSpot[GUARD] = GUARD;
    }

    function addUser(address user, uint256 lpAmount) public onlyOwner{
        require(_nextSpot[user] == address(0));
        address index = _findIndex(lpAmount);
        lpAmounts[user] = lpAmount;
        _nextSpot[user] = _nextSpot[index];
        _nextSpot[index] = user;
        listSize++;
    }

    function increaseLpAmount(address user, uint256 lpAmount) public onlyOwner{
        updateLpAmount(user, lpAmounts[user] + lpAmount);
    }

    function reduceLpAmount(address user, uint256 lpAmount) public onlyOwner{
        updateLpAmount(user, lpAmounts[user] - lpAmount);
    }

    function updateLpAmount(address user, uint256 newLpAmount) public onlyOwner{
        require(_nextSpot[user] != address(0));
        address prevUser = _findPrevUser(user);
        address nextUser = _nextSpot[user];
        if(_verifyIndex(prevUser, newLpAmount, nextUser)){
            lpAmounts[user] = newLpAmount;
        } else {
            removeUser(user);
            addUser(user, newLpAmount);
        }
    }

    function removeUser(address user) public onlyOwner{
        require(_nextSpot[user] != address(0));
        address prevUser = _findPrevUser(user);
        _nextSpot[prevUser] = _nextSpot[user];
        _nextSpot[user] = address(0);
        lpAmounts[user] = 0;
        listSize--;
    }

    function getTop(uint256 k) public view returns(address[] memory) {
        require(k <= listSize);
        address[] memory userLists = new address[](k);
        address currentAddress = _nextSpot[GUARD];
        for(uint256 i = 0; i < k; ++i) {
            userLists[i] = currentAddress;
            currentAddress = _nextSpot[currentAddress];
        }
        return userLists;
    }


    function _verifyIndex(address prevUser, uint256 newValue, address nextUser)
    internal
    view
    returns(bool)
    {
        return (prevUser == GUARD || lpAmounts[prevUser] >= newValue) &&
        (nextUser == GUARD || newValue > lpAmounts[nextUser]);
    }

    function _findIndex(uint256 newValue) internal view returns(address) {
        address candidateAddress = GUARD;
        while(true) {
            if(_verifyIndex(candidateAddress, newValue, _nextSpot[candidateAddress]))
                return candidateAddress;
            candidateAddress = _nextSpot[candidateAddress];
        }
    }

    function _isPrevUser(address user, address prevUser) internal view returns(bool) {
        return _nextSpot[prevUser] == user;
    }

    function _findPrevUser(address user) internal view returns(address) {
        address currentAddress = GUARD;
        while(_nextSpot[currentAddress] != GUARD) {
            if(_isPrevUser(user, currentAddress))
                return currentAddress;
            currentAddress = _nextSpot[currentAddress];
        }
        return address(0);
    }
}
